import React from "react";
import { useTheme } from "../ThemeContext";

export default function ProjectTile(props) {

    const darkMode = useTheme();

    const themeStyles = {
        mainFont: {
            color: darkMode ? 'white' : 'black'
        },
        /*
        tech: {
            backgroundColor: darkMode ? 
        }*/
    }

    return (
        <div className="project-tile">
            <div className="project-thumb-container">
                <img src={require(`../images/${props.data.thumbnail}`)} alt={props.data.thumbDesc} />
            </div>
            <div className="project-tile-content">
                <div className="project-desc-container" style={themeStyles.mainFont}>
                    <p className="project-title">{props.data.title}</p>
                    <p className="project-desc">{props.data.desc}</p>
                </div>
                <div className="tech-used">
                    {props.data.tech.map(tech => <p>{tech}</p>)}
                </div>
                <div className="project-link-container">
                    <a href={props.data.liveLink} target="_blank" rel="noreferrer" className="project-link">Live</a>
                    <a href={props.data.codeLink} target="_blank" rel="noreferrer" className="project-link">Code</a>
                </div>
            </div>
        </div>
    )
}

