import React, {useEffect, useState} from "react";
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faBars, faXmark} from '@fortawesome/free-solid-svg-icons'
import '../Styles/NavBar.css'

export default function NavBar() {

    const [navBarOpen, setNavBarOpen] = useState(false);

    function handleHamburger() {
        setNavBarOpen(prev => !prev)
    }

    function scrollToElement(event){
        var dest;

        switch(event.target.innerText) {
            case 'Home':
                dest = '#welcome-section';
                break;
            case 'About':
                dest = '#about-me';
                break;
            case 'Work':
                dest = '#projects';
                break;
            case 'Contact':
                dest = '#contact';
                break;
        }

        const anchor = document.querySelector(dest);
        anchor.scrollIntoView()
    }

    useEffect(() => {
        const home = document.querySelector('#welcome-section');
        home.scrollIntoView({behavior:'smooth'})
    }, [])


    return (
        <header>
        <nav id="navbar">
            <div id="hamburger-container">
                <span id="hamburger" onClick={handleHamburger}><FontAwesomeIcon icon={navBarOpen ? faXmark : faBars} /></span>
            </div>
            <ul id="navbar-list">
                <li>
                    <span onClick={scrollToElement} className="navbar-link">Home</span>
                </li>
                <li>
                    <span onClick={scrollToElement} className="navbar-link">About</span>
                </li>
                <li>
                    <span onClick={scrollToElement} className="navbar-link">Work</span>
                </li>
                <li>
                    <span onClick={scrollToElement} className="navbar-link">Contact</span>
                </li>
            </ul>
        </nav>
    </header>
    )
}

