import React, {useEffect, useState} from "react";
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faBars, faXmark} from '@fortawesome/free-solid-svg-icons'
import NavLinks from "./NavLinks";
import '../Styles/NavBar.css'

const openNavBarStyle = {
    'display': 'block'
}

const closeNavBarStyle = {
    'display': 'none'
}

export default function NavMenu() {

    const [navBarOpen, setNavBarOpen] = useState(false);

    function handleHamburger() {
        setNavBarOpen(prev => !prev)
    }

    function scrollToElement(event){
        var dest;

        switch(event.target.innerText) {
            case 'Home':
                dest = '#welcome-section';
                break;
            case 'About':
                dest = '#about-me';
                break;
            case 'Work':
                dest = '#projects';
                break;
            case 'Contact':
                dest = '#contact';
                break;
        }

        const anchor = document.querySelector(dest);
        anchor.scrollIntoView()
    }

    useEffect(() => {
        const home = document.querySelector('#welcome-section');
        home.scrollIntoView({behavior:'smooth'})
        
    }, [])

    return (
        <header id="nav-menu">
            <div id="hamburger-container">
                <span id="hamburger" onClick={handleHamburger}><FontAwesomeIcon icon={navBarOpen ? faXmark : faBars} /></span>
            </div>
            <nav id="navbar" style={navBarOpen ? openNavBarStyle : closeNavBarStyle}>
                <ul className="navbar-list-mobile">
                    <li>
                        <span onClick={scrollToElement} className="navbar-link-mobile">Home</span>
                    </li>
                    <li>
                        <span onClick={scrollToElement} className="navbar-link-mobile">About</span>
                    </li>
                    <li>
                        <span onClick={scrollToElement} className="navbar-link-mobile">Work</span>
                    </li>
                    <li>
                        <span onClick={scrollToElement} className="navbar-link-mobile">Contact</span>
                    </li>
                </ul>
            </nav>
        </header>
    )
}
