import React, {useContext} from "react";
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faAngleDown} from '@fortawesome/free-solid-svg-icons'
import '../Styles/MainBanner.css'
import { ThemeContext } from "../Portfolio";

export default function MainBanner() {
    const darkMode = useContext(ThemeContext)

    const themeStyle = {
        backgroundColor: darkMode ? 'black' : 'white',
    }

    function scrollToElement(){;
        const anchor = document.querySelector('#about-me');
        anchor.scrollIntoView()
    }

    return (
        <section id="welcome-section" style={themeStyle}>
            <div id="welcome-content">
                <h1>Hi, I'm <span id="name">Lee Sze.</span></h1>
                <p>Web developer.</p>

                <div id="down-btn-container">
                    <span onClick={scrollToElement} id="scroll-down-btn"><FontAwesomeIcon icon={faAngleDown} /></span>
                </div>
            </div>
        </section>
    )
}
